//12.WAP TO CALCULATE SUM OF NUMBER B/W 1 TO 5.->IN CLASS. 

package code;


public class Program12 {
     static int sum = 0;
     //static int i = 1;
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<5;i++)
		{
			sum = sum +i;
			
		}
		
		System.out.println("sum of 1 to 5  " + sum);
	}

}
