package code;

public class Program69pattern {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
		for(int i=0;i<4;i++)//to increase no of row
		{
			for(int j = 0;j<10;j++)//to increase no of column
			{
				System.out.print("*");
			}
			System.out.println();
		}

	}

}
